/*
 * Overwrite default winston syslog.
 * Config that conform to customed CI syslog logging levels.
 */

'use strict';

var loggerLevelConfig = {};

loggerLevelConfig.levels = {
    debug: 0,
    verbose: 1,
    info: 2,
    metric: 3,
    notice: 4,
    warn: 5,
    error: 6
};

module.exports = loggerLevelConfig;
